sys_variable_value.value:
(function execute(inputs, outputs) {
  outputs.payload = {};

  if (!(inputs.checksums.length === 0)) {
  	outputs.payload.checksums = [];
    outputs.payload.checksums = inputs.checksums;
  } 

  if (!(inputs.paths.length === 0)) {
    outputs.payload.paths = [];
    outputs.payload.paths = inputs.paths;
  }

})(inputs, outputs);

