sys_variable_value.value:
(function execute(inputs, outputs) {
  var provider = new sn_cc.ConnectionInfoProvider();
  var connectionInfo = provider.getConnectionInfo(inputs.connection_id);
  var usr = connectionInfo.getCredentialAttribute("user_name");
  var pwd = connectionInfo.getCredentialAttribute("password");
  var use_mid = connectionInfo.getAttribute("use_mid");

  outputs.user = usr;
  outputs.password = pwd;
  outputs.use_mid = use_mid;
})(inputs, outputs);

