sys_variable_value.value:
(function execute(inputs, outputs) {
  var body = inputs.payload;

  outputs.violation_records = []

  var watchName = body.watch_name;
  var policyName = body.policy_name;
  var issues = body.issues;

  var numOfIssues = issues.length;
  for(var i = 0; i < numOfIssues; i++) {
    outputs.violation_records[i] = {}

    outputs.violation_records[i].watch_name = watchName;
    outputs.violation_records[i].policy_name = policyName;
    outputs.violation_records[i].severity = issues[i].severity;
    outputs.violation_records[i].issue_type = issues[i].type;
    outputs.violation_records[i].created_on = issues[i].created;
    outputs.violation_records[i].summary = issues[i].summary;
    outputs.violation_records[i].cve = issues[i].cve;
  }
})(inputs, outputs);

