sys_variable_value.value:
(function execute(inputs, outputs) {
  outputs.payload = {};
  outputs.payload.key = inputs.key;
  outputs.payload.description = inputs.description;
  outputs.payload.enabled = inputs.enabled;
  outputs.payload.event_filter = {};
  outputs.payload.event_filter.domain = "artifact";
  outputs.payload.event_filter.event_types = inputs.event_types.split(',');

  outputs.payload.event_filter.criteria = {};
  outputs.payload.event_filter.criteria.anyLocal = true;
  outputs.payload.event_filter.criteria.anyRemote = false;
  outputs.payload.event_filter.criteria.includePatterns = [];
  outputs.payload.event_filter.criteria.excludePatterns = [];
  outputs.payload.event_filter.criteria.repoKeys = [];

  outputs.payload.handlers = [];
  outputs.payload.handlers[0] = {};
  outputs.payload.handlers[0].handler_type = "webhook";
  outputs.payload.handlers[0].url = inputs.webhook.url;
  outputs.payload.handlers[0].secret = inputs.webhook.secret;
  outputs.payload.handlers[0].proxy = inputs.webhook.proxy;
  outputs.payload.handlers[0].custom_http_headers = inputs.webhook.custom_http_headers;
})(inputs, outputs);

