sys_variable_value.value:
(function execute(inputs, outputs) {
  outputs.violation_report = {};
  //outputs.violation_report.name = "";
  outputs.violation_report.name = inputs.name;
  outputs.violation_report.resources = {};
  outputs.violation_report.resources.repositories = inputs.repositories;
  //outputs.violation_report.repositories = inputs.repositories;
})(inputs, outputs);

