sys_variable_value.value:
(function execute(inputs, outputs) {
  outputs.url = inputs.url;
  outputs.payload = {};

  outputs.payload.notes = "ignore '" + inputs.cve + "' until the expiration date set";

  var today = new Date();
  var nextWeek = new Date(today.getFullYear(), today.getMonth(), today.getDate()+7);

  outputs.payload.expires_at = nextWeek.toISOString();
  outputs.payload.ignore_filters = {};
  outputs.payload.ignore_filters.cves = [];
  outputs.payload.ignore_filters.cves[0] = inputs.cve;   
})(inputs, outputs);

