sys_variable_value.value:
(function execute(inputs, outputs) {
  webhook_model = {};
  webhook_model.name = inputs.webhook_name;
  webhook_model.description = inputs.description;
  webhook_model.url = inputs.webhook_url;

  webhook_model.headers = {};
  hdrCount = inputs.headers.length;
  for(var i = 0; i < hdrCount; i++) {
    webhook_model.headers[inputs.headers[i].name] = inputs.headers[i].value;
  }

  outputs.webhook_model = JSON.stringify(webhook_model);
})(inputs, outputs);

