sys_variable_value.value:
(function execute(inputs, outputs) {
  outputs.policy_update_model = {};
  outputs.policy_update_model.name = inputs.policy_name;
  outputs.policy_update_model.type = inputs.policy_type;

  outputs.policy_update_model.rules = [];
  outputs.policy_update_model.rules[0] = {};
  outputs.policy_update_model.rules[0].name = inputs.rule_name;
  outputs.policy_update_model.rules[0].priority = inputs.rule_priority;

  outputs.policy_update_model.rules[0].criteria = {};
  outputs.policy_update_model.rules[0].criteria.min_severity = inputs.rule_min_severity;

  outputs.policy_update_model.rules[0].actions = {};
  outputs.policy_update_model.rules[0].actions.webhooks = [];
  outputs.policy_update_model.rules[0].actions.webhooks[0] = inputs.webhook_name;
})(inputs, outputs);

