sys_variable_value.value:
(function execute(inputs, outputs) {
  respBody = JSON.parse(inputs.watch_response_body);
  outputs.policies = [];
  listOfPolicies = respBody.assigned_policies;

  for(var i = 0; i < listOfPolicies.length; i++) {
    outputs.policies[i] = {};
    outputs.policies[i].name = listOfPolicies[i].name;
    outputs.policies[i].type = listOfPolicies[i].type;
  }
})(inputs, outputs);

